import React from 'react'
import {connect} from 'react-redux'
import R from 'ramda'
import {Link} from 'react-router'

import {
  removeProductFromBasket,
  cleanBasket, 
  basketCheckout
} from 'actions'

import {getBasketProductsWithCount, getTotalBasketPrice} from 'selectors'

const Basket = ({products, totalPrice, removeProductFromBasket, cleanBasket, basketCheckout}) => {

  const isBasketEmpty = R.isEmpty(products)
  
  const renderContent = () => {
    return (
    <div>
      {isBasketEmpty && <div>Your shopping cart is empty</div>}
      <div className='table-responsive'>
        <table className='table-bordered table-striped table-condesed cf'>
          <tbody>
            {products.map((product, index) => (
              <tr key={index} className='item-checkout'>
              <td className='first-column-checkout'>
                <img 
                  classname='img-thumbnail'
                  src={product.image}
                  alt={product.name}
                />
              </td>
              <td>{product.name}</td>
              <td>${product.price}</td>
              <td>{product.count}</td>
              <td>
                <span className='delete-cart'
                      onClick={() => removeProductFromBasket(product.id)}>
                 </span>
              </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      { R.not(isBasketEmpty) &&
        <div className='row'>
          <div className='pull-right total-user-checkout'>
            <b>Total:</b>
            ${totalPrice}
          </div>
        </div>
      }
    </div>
    )
  }

  const renderSidebar = () => (
    <div>
      <Link className='btn btn-info'
            to='/'>
        <span className='glyphicon glyphicon-icon-sign' />
        Continue shopping
      </Link>

      {
        R.not(isBasketEmpty) &&
        <div>
          <button 
            onClick={() => cleanBasket()}
            className='btn btn-danger'>
            <span className='glyphicon glyphicon-trash' />
            Clean cart
          </button>
          <button 
            onClick={() => basketCheckout(products)}
            className='btn btn-success'>
            <span className='glyphicon glyphicon-envelope' />
            Checkout
          </button>
        </div>
      }
    </div>
  )

  return (
     <div className='view-container'>
       <div className='container'>
         <div className='row'>
           <div className='col-md-9'>
             {renderContent()}
           </div>
           <div className='col-md-3 btn user-checkout'>
             {renderSidebar()}
           </div>
         </div>
       </div>
     </div>
  )
}

const mapStateToProps = state => {
  return {
  products: getBasketProductsWithCount(state),
  totalPrice: getTotalBasketPrice(state)
  }
}

const mapDispatchToProps = {
  removeProductFromBasket,
  cleanBasket, 
  basketCheckout
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)